  public void quizSucheAnzeigen(String[] pSuche){
    quizListArea.setText("");
    for (int i=0; i<pSuche.length; i++) {
      String[] quiz=pSuche[i].split("--");
      String erg="";
      for (int a=0; a<quiz.length; a++) {
        erg+=quiz[a]+" ";
      }
      quizListArea.setText(quizListArea.getText()+erg+"\n"); 
    }
  } 
